def create_default_user(user_id: int, referrer_id: int = None):
    """–°–æ–∑–¥–∞–Ω–∏–µ –Ω–æ–≤–æ–≥–æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –≤ –±–∞–∑–µ –¥–∞–Ω–Ω—ã—Ö —Å –Ω–∞—Å—Ç—Ä–æ–π–∫–∞–º–∏ –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é"""
    try:
        connection = connect_to_db()
        cursor = connection.cursor()

        cursor.execute("""
            INSERT INTO users (
                user_id, daily_tokens, last_reset, total_spent,
                referral_count, input_tokens, output_tokens,
                invited_users, referrer_id, subscription_plan
            ) VALUES (
                %s, 30000, CURRENT_DATE, 0.0,
                0, 0, 0, 0, %s, 'free'
            )
            RETURNING daily_tokens, last_reset, total_spent,
                      referral_count, input_tokens, output_tokens,
                      invited_users, referrer_id, subscription_plan
        """, (user_id, referrer_id))

        # –ü–æ–ª—É—á–∞–µ–º —Å–æ–∑–¥–∞–Ω–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ
        user = cursor.fetchone()

        connection.commit()
        cursor.close()
        connection.close()

        # –í–æ–∑–≤—Ä–∞—â–∞–µ–º –≤ –Ω—É–∂–Ω–æ–º —Ñ–æ—Ä–º–∞—Ç–µ
        return {
            "user_id": user_id,
            "daily_tokens": user[0],
            "last_reset": str(user[1]),
            "total_spent": float(user[2]),
            "referral_count": user[3],
            "input_tokens": user[4],
            "output_tokens": user[5],
            "invited_users": user[6],
            "referrer_id": user[7],
            "subscription_plan": user[8]
        }
    except Exception as e:
        print(f"–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ–∑–¥–∞–Ω–∏–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è: {e}")
        return None

def load_user_data(user_id: int):
    """–ó–∞–≥—Ä—É–∑–∫–∞ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –∏–∑ –±–∞–∑—ã –¥–∞–Ω–Ω—ã—Ö –≤ —Ñ–æ—Ä–º–∞—Ç–µ –¥–ª—è —Å—É—â–µ—Å—Ç–≤—É—é—â–∏—Ö —Ñ—É–Ω–∫—Ü–∏–π."""
    connection = None
    try:
        connection = connect_to_db()
        cursor = connection.cursor()
        
        cursor.execute("""
            SELECT daily_tokens, last_reset, total_spent,
                   referral_count, input_tokens, output_tokens,
                   invited_users, referrer_id, subscription_plan
            FROM users
            WHERE user_id = %s
        """, (user_id,))
        
        user = cursor.fetchone()

        if user:
            # –í–æ–∑–≤—Ä–∞—â–∞–µ–º –¥–∞–Ω–Ω—ã–µ –≤ —Ñ–æ—Ä–º–∞—Ç–µ —Å–ª–æ–≤–∞—Ä—è
            return {
                "user_id": user_id,
                "daily_tokens": user[0],
                "last_reset": str(user[1]),  # –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –¥–∞—Ç—É –≤ —Å—Ç—Ä–æ–∫—É –¥–ª—è —Å–æ–≤–º–µ—Å—Ç–∏–º–æ—Å—Ç–∏
                "total_spent": float(user[2]),  # –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –≤ float –¥–ª—è —Å–æ–≤–º–µ—Å—Ç–∏–º–æ—Å—Ç–∏
                "referral_count": user[3],
                "input_tokens": user[4],
                "output_tokens": user[5],
                "invited_users": user[6],
                "referrer_id": user[7],
                "subscription_plan": user[8]
            }

        return None  # –í–æ–∑–≤—Ä–∞—Ç None, –µ—Å–ª–∏ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å –Ω–µ –Ω–∞–π–¥–µ–Ω

    except Exception as e:
        print(f"–û—à–∏–±–∫–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è: {e}")
        return None  # –í–æ–∑–≤—Ä–∞—Ç None –≤ —Å–ª—É—á–∞–µ –æ—à–∏–±–∫–∏

    finally:
        if connection:
            cursor.close()
            connection.close()  # –ó–∞–∫—Ä—ã–≤–∞–µ–º —Å–æ–µ–¥–∏–Ω–µ–Ω–∏–µ –≤ –ª—é–±–æ–º —Å–ª—É—á–∞–µ

def save_user_data(user_data: dict):
    """–°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –≤ –±–∞–∑—É –¥–∞–Ω–Ω—ã—Ö"""
    try:
        connection = connect_to_db()
        cursor = connection.cursor()
        
        cursor.execute("""
            INSERT INTO users (
                user_id, daily_tokens, last_reset, total_spent,
                referral_count, input_tokens, output_tokens,
                invited_users, referrer_id, subscription_plan
            ) VALUES (%s, %s, %s, %s, %s, %s, %s, %s, %s, %s)
            ON CONFLICT (user_id) DO UPDATE SET
                daily_tokens = EXCLUDED.daily_tokens,
                last_reset = EXCLUDED.last_reset,
                total_spent = EXCLUDED.total_spent,
                referral_count = EXCLUDED.referral_count,
                input_tokens = EXCLUDED.input_tokens,
                output_tokens = EXCLUDED.output_tokens,
                invited_users = EXCLUDED.invited_users,
                referrer_id = EXCLUDED.referrer_id,
                subscription_plan = EXCLUDED.subscription_plan
        """, (
            user_data["user_id"],
            user_data["daily_tokens"],
            user_data["last_reset"],
            user_data["total_spent"],
            user_data["referral_count"],
            user_data["input_tokens"],
            user_data["output_tokens"],
            user_data["invited_users"],
            user_data["referrer_id"],
            user_data["subscription_plan"]
        ))
        
        connection.commit()
        cursor.close()
        connection.close()
        
        return True
        
    except Exception as e:
        print(f"–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è: {e}")
        return False
    
