def store_message_in_db(chat_id, role, content):
    """–°–æ—Ö—Ä–∞–Ω—è–µ—Ç —Å–æ–æ–±—â–µ–Ω–∏–µ –≤ –±–∞–∑—É –¥–∞–Ω–Ω—ã—Ö –∏ –∫—ç—à–∏—Ä—É–µ—Ç –µ–≥–æ –≤ Redis."""
    conn = connect_to_db()
    cur = conn.cursor()

    # –°–æ—Ö—Ä–∞–Ω—è–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –≤ –±–∞–∑—É –¥–∞–Ω–Ω—ã—Ö
    cur.execute("""
        INSERT INTO chat_history (chat_id, role, content, timestamp)
        VALUES (%s, %s, %s, NOW())
    """, (chat_id, role, content))

    conn.commit()
    cur.close()
    conn.close()

    # –ö—ç—à–∏—Ä—É–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –≤ Redis
    cache_key = f'chat_history_{chat_id}'

    # –ü–æ–ª—É—á–∞–µ–º —Ç–µ–∫—É—â—É—é –∏—Å—Ç–æ—Ä–∏—é –∏–∑ –∫—ç—à–∞ (–µ—Å–ª–∏ –µ—Å—Ç—å)
    history = r.lrange(cache_key, 0, -1)

    # –î–æ–±–∞–≤–ª—è–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –≤ –∏—Å—Ç–æ—Ä–∏—é –∏ –æ–≥—Ä–∞–Ω–∏—á–∏–≤–∞–µ–º —Ä–∞–∑–º–µ—Ä
    message = json.dumps({"role": role, "content": content})
    r.rpush(cache_key, message)

    # –û–≥—Ä–∞–Ω–∏—á–∏–≤–∞–µ–º —Ä–∞–∑–º–µ—Ä –∫—ç—à–∞ (–Ω–∞–ø—Ä–∏–º–µ—Ä, –¥–æ 100 —Å–æ–æ–±—â–µ–Ω–∏–π)
    if r.llen(cache_key) > 100:
        r.lpop(cache_key)  # –£–¥–∞–ª—è–µ–º —Å—Ç–∞—Ä–æ–µ —Å–æ–æ–±—â–µ–Ω–∏–µ

    print(f"–°–æ–æ–±—â–µ–Ω–∏–µ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–æ –≤ –±–∞–∑–µ –¥–∞–Ω–Ω—ã—Ö –∏ –∫—ç—à–µ Redis –¥–ª—è chat_id {chat_id}.")

def get_chat_history(chat_id, limit=10):
    """–ü–æ–ª—É—á–∞–µ—Ç –∏—Å—Ç–æ—Ä–∏—é —á–∞—Ç–∞ –∏–∑ –±–∞–∑—ã –¥–∞–Ω–Ω—ã—Ö –±–µ–∑ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è –∫—ç—à–∞."""
    conn = connect_to_db()
    cur = conn.cursor()

    cur.execute("""
        SELECT role, content FROM chat_history
        WHERE chat_id = %s
        ORDER BY timestamp DESC
        LIMIT %s
    """, (chat_id, limit))

    history = [{"role": role, "content": content} for role, content in cur.fetchall()]

    cur.close()
    conn.close()

    print(f"–ò—Å—Ç–æ—Ä–∏—è —á–∞—Ç–∞ –¥–ª—è chat_id {chat_id} –ø–æ–ª—É—á–µ–Ω–∞ –∏–∑ –±–∞–∑—ã –¥–∞–Ω–Ω—ã—Ö.")
    return history[::-1]  # –û–±—Ä–∞—Ç–Ω–æ –≤ —Ö—Ä–æ–Ω–æ–ª–æ–≥–∏—á–µ—Å–∫–æ–º –ø–æ—Ä—è–¥–∫–µ

def clear_chat_history(chat_id):
    """–û—á–∏—â–∞–µ—Ç –∏—Å—Ç–æ—Ä–∏—é —á–∞—Ç–∞ –≤ –±–∞–∑–µ –¥–∞–Ω–Ω—ã—Ö"""
    conn = connect_to_db()
    cur = conn.cursor()
    cur.execute("DELETE FROM chat_history WHERE chat_id = %s", (chat_id,))
    conn.commit()
    cur.close()
    conn.close()


