def check_assistants_in_database(connection):
    with connection.cursor() as cursor:
        cursor.execute("SELECT assistant_key, name FROM assistants;")
        assistants = cursor.fetchall()
        print("–ê—Å—Å–∏—Å—Ç–µ–Ω—Ç—ã –≤ –±–∞–∑–µ –¥–∞–Ω–Ω—ã—Ö:")
